#define PEANUT_GB_HEADER_ONLY
#include "common.h"
// _____________________________________________________________________________
//
//  Very basic Game Boy audio implementation for MediaTek MRE.
// 
//  Instead of playing raw wave data (which doesn't seem to be possible) this 
//  plays back channel 2 (usually the main melody channel in GB games) as MIDI
//  notes.
// 
//  MRE's MIDI implementation is rather minimal - it doesn't allow playing
//  individual notes (so MIDI files are our only choice), and only one MIDI file
//  can play at once. Despite MRE's documentation saying you can play multiple
//  MIDI files at a time, this did not work on my Nokia 215, so I chose to only
//  play back GB channel 2, as it gave me the best results.
// 
//  There are two ways to play MIDI in MRE:
//  * vm_midi_play (using MRE's resource format, no thanks)
//  * vm_midi_play_by_bytes (playing from a normal data array; I went with this)
//  There are also _ex variants of these two functions, which allow you to
//  specify which speaker to play from.
// _____________________________________________________________________________
//

// _____________________________________________________________________________
//
//  Minimal MIDI file that plays one note for a short time
//  To change the note being played, modify offsets 24 and 28
//  To change the velocity of the note, modify offsets 25 and 29
// _____________________________________________________________________________
//
unsigned char midi_note[] = {
    // MIDI Header
    0x4d, 0x54, 0x68, 0x64,  // "MThd" identifier
    0x00, 0x00, 0x00, 0x06,  // Length of the rest of the header (6 in big endian)
    0x00, 0x00,  // Format = 0 (single track)
    0x00, 0x01,  // Amount of tracks
    0x00, 0x60,  // Division (ticks per quarter-note)

    // MIDI Track
    0x4d, 0x54, 0x72, 0x6b,  // "MTrk" identifier
    0x00, 0x00, 0x00, 0x0c,  // Length of the rest of the track
    0x00, 0x90, 0x3c, 0x7f,  // Note on event (0x3c (60) is the key, 0x7f (127) is velocity)
    0x08, 0x80, 0x3c, 0x7f,  // Note off event (same as above applies)
    0x08, 0xff, 0x2f, 0x00,  // End of track
};
unsigned int midi_note_len = 34;

// _____________________________________________________________________________
//
union {
    uint8_t bytes[2];
    uint16_t full;
} period;
uint16_t last_period;
int midi_handle;

static void midi_handler(VMINT handle, VMINT event) {}

uint8_t audio_midi_read(const uint16_t addr) { return 0; }

