void audio_midi_update() {
    if (period.full == last_period) return;
    
    midi_note[24] = period_to_midi[period.full];
    midi_note[28] = period_to_midi[period.full];

    // FIXME: vm_midi_stop takes about 70 ms to run?!
    if (midi_handle != -1) vm_midi_stop(midi_handle);
    midi_handle = vm_midi_play_by_bytes(midi_note, midi_note_len, 1, NULL);

    last_period = period.full;
}

void audio_midi_write(const uint16_t addr, const uint8_t val) {
    // Reference: https://gbdev.io/pandocs/Audio_Registers.html
    switch (addr) {
        case 0xFF18: {
            period.bytes[0] = val;
            break;
        }
        case 0xFF19: {
            period.bytes[1] = val & 0b111;
            break;
        }
    }
}